import './Styles/App.css';
import { Routes, Route, ProtectedRoute } from 'react-router-dom';
import Login from './Components/Login';
import Register from './Components/Register';
import Register2 from './Components/Register2';
import Home from './Components/Home';
import Dashboard from './Components/Dashboard';
import Appointments from './Components/Appointments';
import Settings from './Components/Settings';
import Topnav from './Components/Topnav';
import Navbar from './Components/Navbar';
import Clients from './Components/Clients';
import Technicians from './Components/Technicians';
import { useState } from 'react';

function App() {
  //const name = 'Nikoos';
  //const surname = 'Korompos';

  const [userData, setUserData] = useState({ name: '', surname: '' });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('/api/user/userId'); // Replace with the actual route
        setUserData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array ensures the effect runs once

  const { name, surname } = userData;


  const [isAdmin, setIsAdmin] = useState(false);

  const handleIsAdmin = (data) => {
    setIsAdmin(data);
  };

  return (
    <div className='main'>
      <Topnav />
      <div className='main-wrapper'>
        <Navbar name={name} surname={surname} isAdmin={isAdmin} />
        <div className='components-wrapper'>
          <Routes>
            <Route path='/' element={<Home />} />
            <Route
              path='/login'
              element={<Login handleIsAdmin={handleIsAdmin} />}
            />
            <Route path='/register' element={<Register />} />
            <Route path='/register_2' element={<Register2 />} />
            <Route
              path='/dashboard'
              element={
                <Dashboard name={name} surname={surname} isAdmin={isAdmin} />
              }
            />
            <Route path='/appointments' element={<Appointments />} />
            <Route path='/technicians' element={<Technicians />} />
            <Route path='/clients' element={<Clients />} />
            <Route path='/settings' element={<Settings />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
