import '../Styles/Dashboard.css';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from '../axios/axios';
import calendar from '../Assets/calendar.svg';
import calendar_white from '../Assets/calendar_white.svg';
import AppointmentForm from './AppointmentForm';

const Dashboard = (props) => {
  const [name, setName] = useState(props.name);
  const [src, setSrc] = useState(calendar);
  const [showForm, setShowForm] = useState(false);

  const appointments = [
    {
      id: 1,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: 'Active',
      price: 100,
    },
    {
      id: 2,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: 'Active',
      price: 100,
    },
    {
      id: 3,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: 'Active',
      price: 100,
    },
    {
      id: 4,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: 'Active',
      price: 100,
    },
  ];

  const handleShowForm = (data) => {
    setShowForm(data);
  };

  return (
    <div className='dashboard-wrapper'>
      <div className='dashboard-header'>
        <h1>Hello, {name}</h1>
        <button
          onMouseOver={(e) => setSrc(calendar_white)}
          onMouseOut={(e) => setSrc(calendar)}
          onClick={(e) => setShowForm(true)}
        >
          <img src={src} alt='calendar' />
          New appointment
        </button>
        {showForm && <AppointmentForm handleShowForm={handleShowForm} />}
      </div>
      <div className='active-appointments'>
        <p className='half-width-border'>Your appointments</p>
        {/* <div className='appointments-wrapper'> */}
        {appointments.map((appointment) => (
          <div className='appointment-card' key={appointment.id}>
            <div className='appointment-card-header'>
              <p>{appointment.date}</p>
              <p>{appointment.time}</p>
              <p>{appointment.technician}</p>
              <p>{appointment.type}</p>
              <p>{appointment.status}</p>
              <p>{appointment.price}</p>
            </div>
          </div>
        ))}
        {/* </div> */}
      </div>
    </div>
  );
};

export default Dashboard;
