import { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import '../Styles/Register.css';
import doodle from '../Assets/doodle.jpg';

const Register2 = (props) => {
  const [register, setRegister] = useState(false);
  const [fName, setFirstName] = useState('');
  const [lName, setLastName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [step, setStep] = useState(2);

  const handleSubmit = (e) => {
    e.preventDefault();
    setRegister(false);
    console.log('username:  ', props.username);
    console.log('email:  ', props.email);
    console.log('password:  ', props.password);
  };

  return (
    <>
      {register ? (
        <Navigate to='/dashboard' />
      ) : (
        <div className={`register-main-step ${step === 1 ? 'hidden' : ''}`}>
          {/* <div className='side-doodle'>
            <img src={doodle} alt='doodle' className='doodle' />
          </div> */}
          {/* <div className='register-form-wrapper'> */}
          <div className='register-form'>
            {/* <h1>Sign up</h1> */}
            {/* <div className='progress-bar'>
              <div className='progress-1'></div>
              <div className='progress-2'></div>
            </div> */}
            <form onSubmit={handleSubmit} className='form form-second-step'>
              <div className='name-surname'>
                <div>
                  <label>First name</label>
                  <input
                    type='text'
                    id='fname'
                    autoComplete='off'
                    onChange={(e) => setFirstName(e.target.value)}
                    value={fName}
                    required
                    placeholder=''
                  />
                </div>
                <div>
                  <label>Last name</label>
                  <input
                    type='text'
                    id='lname'
                    onChange={(e) => setLastName(e.target.value)}
                    value={lName}
                    required
                    placeholder=''
                  />
                </div>
              </div>
              <label>Phone</label>
              <input
                type='tel'
                id='phone'
                onChange={(e) => setPhone(e.target.value)}
                value={phone}
                required
                placeholder=''
              />

              <label>Address</label>
              <input
                type='text'
                id='address'
                onChange={(e) => setAddress(e.target.value)}
                value={address}
                required
                placeholder=''
              />

              <Link to='/login'>Already have an account? Sign in.</Link>
              <div>
                <button type='submit' id='back-btn'>
                  Back
                </button>

                <button type='submit'>Sign up</button>
              </div>
            </form>
          </div>
        </div>
        // </div>
      )}
    </>
  );
};

export default Register2;
