import '../Styles/Dashboard.css';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from '../axios/axios';
import calendar from '../Assets/calendar.svg';
import calendar_white from '../Assets/calendar_white.svg';
import AppointmentForm from './AppointmentForm';
import AppointmentCard from './AppointmentCard';

const ADMIN_URL = '/auth/check-admin';

const Dashboard = (props) => {
  const [name, setName] = useState(props.name);
  const [src, setSrc] = useState(calendar);
  const [showForm, setShowForm] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const response = await axios.get(ADMIN_URL, {
          headers: { 'Content-Type': 'application/json' },
          withCredentials: true,
        });
        if (true) {
          console.log(response.data);
          setIsAdmin(response.data);
          console.log('Admin is true' + isAdmin);
        }
      } catch (error) {
        console.log(error);
      }
    };

    // Initial check when the component mounts
    checkAdmin();

    // Set up a repeating interval to check admin status
    const intervalId = setInterval(checkAdmin, 60000); // Check every 60 seconds

    // Clean up the interval when the component unmounts
    return () => {
      clearInterval(intervalId);
    };
  }, []);

  const appointments = [
    {
      id: 1,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: true,
      price: 100,
    },
    {
      id: 2,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: true,
      price: 100,
    },
    {
      id: 3,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: true,
      price: 100,
    },
    {
      id: 4,
      date: '2021-10-10',
      time: '10:00',
      technician: 'John Doe',
      type: 'Repair',
      status: true,
      price: 100,
    },
  ];

  const handleShowForm = (data) => {
    setShowForm(data);
  };

  return (
    <div className='dashboard-wrapper'>
      <div className='dashboard-header'>
        <h1>Hello, {name}</h1>
        <button
          onMouseOver={(e) => setSrc(calendar_white)}
          onMouseOut={(e) => setSrc(calendar)}
          onClick={(e) => setShowForm(true)}
        >
          <img src={src} alt='calendar' />
          New appointment
        </button>
        {showForm && <AppointmentForm handleShowForm={handleShowForm} />}
      </div>
      <div className='active-appointments'>
        <p className='half-width-border'>Your appointments</p>
        <div className='appointments-wrapper'>
          <AppointmentCard appointment={appointments} isAdmin={isAdmin} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
