import '../Styles/AppointmentCard.css';
import calendar from '../Assets/calendar.svg';
import time from '../Assets/time.svg';
import user from '../Assets/user.svg';
import typeService from '../Assets/type.svg';
import euro from '../Assets/euro.svg';
import axios from "../axios/axios.js";
import React from "react";
import { useState,useEffect } from 'react';


//const baseURL = "http://localhost.localdomain:8800/api/appointments";

const GETACTIVITY_URL = '/appointments/64e8f2582c0d7fe225a319d6';

//
//function App() {
  //  const [appointment, setAppointment] = React.useState(null);
  //
  //  React.useEffect(() => {
    //    axios.get(baseURL).then((response) => {
      //      setAppointment(response.data);
      //    });
      //  }, []);
      //
      //  if (!appointment) return null; }


const AppointmentCard = ({ appointment, isAdmins }) => {
  // const isAdmin = props.isAdmin;
  const [isAdmin, setIsAdmin] = useState(false);
  console.log('is this user an admin?' + isAdmins);

  const [data, setData] = useState([]);
  const [load, setLoad] = useState(false);


//  useEffect(() => {
//    handleSubmit();
//    setLoad(false);
//  }, []);
//
//  const handleSubmit = async () => {
//    try {
//      const response = await axios.get(GETACTIVITY_URL, {
//        headers: { 'Content-Type': 'application/json' },
//      });
//      setData(response.data);
//    } catch (err) {}
//    setLoad(true);
//  };


useEffect(() => {
  axios.get('/appointments/64e8f2582c0d7fe225a319d6')
    .then(response => {
      setData(response.data);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
    });
}, []);


  if (!appointment) return null;

  return appointment.length === 0 ? (
    <div className='appointments-message'>
      <h2>No appointments yet</h2>
    </div>
  ) : (
    <div className='appointment-cards'>
      {appointment.map((appointment) => (
        // <div className='appointment-card' key={appointment.id}>
        <div className='appointment-card-header' key={appointment.id}>
          <p>
            <img src={calendar} alt='calendar' />
            {appointment.date}
          </p>
          <p>
            <img src={time} alt='calendar' />
            {appointment.time}
          </p>
          <p>
            <img src={user} alt='calendar' />
            {appointment.technician._id}
          </p>
          <p>
            <img src={typeService} alt='calendar' />
            {appointment.service_id}
          </p>
          <p>
            <img src={euro} alt='calendar' />
            {appointment.price}
          </p>
          {isAdmins && <button>Completed</button>}

          <div className={appointment.status === true ? 'status' : ''}></div>
        </div>
        // </div>
      ))}
    </div>
  );
};

export default AppointmentCard;
