import '../Styles/ClientForm.css';
import { useState } from 'react';

const ClientForm = (props) => {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [telephone, setTelephone] = useState('');
  const [address, setAddress] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    props.handleShowForm(false);
  };

  const handleShowForm = () => {
    props.handleShowForm(false);
  };

  return (
    <div className='client-form-wrapper '>
      <form className='client-form scale-up-center'>
        <h2>Add new client</h2>
        <label>Name</label>
        <input
          type='text'
          name='name'
          id='name'
          autoComplete='off'
          onChange={(e) => setName(e.target.value)}
          value={name}
          required
        />
        <label>Surname</label>
        <input
          type='text'
          name='surname'
          id='surname'
          autoComplete='off'
          onChange={(e) => setSurname(e.target.value)}
          value={surname}
          required
        />
        <label>Select type of client</label>
        <select
          name='type'
          id='type'
          onChange={(e) => setType(e.target.value)}
          value={type}
          required
        >
          <option value='default'>Select type</option>
          <option value='option 1'>Select type</option>
          <option value='option 2'>Select type</option>
          <option value='option 3'>Select type</option>
        </select>
        <div className='form-buttons'>
          <button onClick={handleShowForm}>Cancel</button>
          <button onClick={handleSubmit} id='submit-button'>
            Book client
          </button>
        </div>
      </form>
    </div>
  );
};

export default ClientForm;
